INSERT INTO [Settings].[Localization] ([LanguageId],[ResourceKey],[ResourceValue]) VALUES (1,'Admin.Settings.Template.MainPageModeHint', 'Режим отображения главной страницы <br/>Структура сайта может быть представлена в двухколоночном и одноколоночном режиме.<br/><br/> Инструкция:<br/><a href="https://www.advantshop.net/help/pages/template-settings#11" target="_blank">https://www.advantshop.net/help/pages/template-settings#11</a>')
INSERT INTO [Settings].[Localization] ([LanguageId],[ResourceKey],[ResourceValue]) VALUES (2,'Admin.Settings.Template.MainPageModeHint', 'Homepage display mode <br/>The site structure can be presented in two-column and single-column mode.<br/><br/>  Instruction:<br/> <a href="https://www.advantshop.net/help/pages/template-settings#11" target="_blank">https://www.advantshop.net/help/pages/template-settings#11</a>')
INSERT INTO [Settings].[Localization] ([LanguageId],[ResourceKey],[ResourceValue]) VALUES (1,'Admin.Settings.Template.SearchBlockLocationHint', 'Расположение поиска<br/>Данная настройка отвечает за то, в какой области на сайте будет отображаться окно поиска.<br/><br/> Инструкция:<br/><a href="https://www.advantshop.net/help/pages/template-settings#13" target="_blank">https://www.advantshop.net/help/pages/template-settings#13</a>')
INSERT INTO [Settings].[Localization] ([LanguageId],[ResourceKey],[ResourceValue]) VALUES (2,'Admin.Settings.Template.SearchBlockLocationHint', 'Search Location<br/>This setting is responsible for the area in which the search box will be displayed on the site.<br/><br/>  Instruction:<br/> <a href="https://www.advantshop.net/help/pages/template-settings#13" target="_blank">https://www.advantshop.net/help/pages/template-settings#13</a>')
INSERT INTO [Settings].[Localization] ([LanguageId],[ResourceKey],[ResourceValue]) VALUES (1,'Admin.Settings.Template.RecentlyViewVisibilityHint', 'Вы уже смотрели<br/>Настройка позволяет отображать список товаров, которые клиенты просматривали ранее на сайте<br/><br/> Инструкция:<br/><a href="https://www.advantshop.net/help/pages/template-settings#6" target="_blank">https://www.advantshop.net/help/pages/template-settings#6</a>')
INSERT INTO [Settings].[Localization] ([LanguageId],[ResourceKey],[ResourceValue]) VALUES (2,'Admin.Settings.Template.RecentlyViewVisibilityHint', 'You already watched<br/>The setting allows you to display a list of products that customers viewed previously on the site.<br/><br/>  Instruction:<br/> <a href="https://www.advantshop.net/help/pages/template-settings#6" target="_blank">https://www.advantshop.net/help/pages/template-settings#6</a>')
INSERT INTO [Settings].[Localization] ([LanguageId],[ResourceKey],[ResourceValue]) VALUES (1,'Admin.Settings.Template.WishListVisibilityHint', 'Список желаний<br/>Активность данной настройки позволяет клиентам добавлять товары в свой список желаний.<br/><br/> Инструкция:<br/><a href="https://www.advantshop.net/help/pages/template-settings#6" target="_blank">https://www.advantshop.net/help/pages/template-settings#6</a>')
INSERT INTO [Settings].[Localization] ([LanguageId],[ResourceKey],[ResourceValue]) VALUES (2,'Admin.Settings.Template.WishListVisibilityHint', 'A wish list<br/>The activity of this setting allows customers to add products to their wish list.<br/><br/>  Instruction:<br/> <a href="https://www.advantshop.net/help/pages/template-settings#6" target="_blank">https://www.advantshop.net/help/pages/template-settings#6</a>')
INSERT INTO [Settings].[Localization] ([LanguageId],[ResourceKey],[ResourceValue]) VALUES (1,'Admin.Settings.SystemSettings.ShowCookiesPolicyMessageNote', 'Показывать уведомление об использовании cookies<br/>Настройка позволяет выводить для всех посетителей сайта всплывающее окно с уведомлением о том, что для функционирования сайта Вы осуществляете сбор данных пользователя (cookie, данные об IP-адресе и местоположении) в соответствии с законом 152-ФЗ.<br/><br/> Инструкция:<br/><a href="https://www.advantshop.net/help/pages/152-fz" target="_blank">https://www.advantshop.net/help/pages/152-fz</a>')
INSERT INTO [Settings].[Localization] ([LanguageId],[ResourceKey],[ResourceValue]) VALUES (2,'Admin.Settings.SystemSettings.ShowCookiesPolicyMessageNote', 'Show cookie notification<br/>The setting allows you to display a pop-up window for all site visitors with a notification that for the operation of the site you are collecting user data (cookies, IP address and location data) in accordance with Law 152-FZ.<br/>  Instruction:<br/> <a href="https://www.advantshop.net/help/pages/152-fz" target="_blank">https://www.advantshop.net/help/pages/152-fz</a>')
INSERT INTO [Settings].[Localization] ([LanguageId],[ResourceKey],[ResourceValue]) VALUES (1,'Admin.Settings.SystemSettings.SiteLanguageNote', 'Язык магазина<br/>Настройка позволяет сменить язык магазина (меняются кнопки, панель администрирования, товары останутся на том языке, на котором заполняли магазин).<br/><br/> Инструкция:<br/><a href="https://www.advantshop.net/help/pages/change-buttons#4" target="_blank">https://www.advantshop.net/help/pages/change-buttons#4</a>')
INSERT INTO [Settings].[Localization] ([LanguageId],[ResourceKey],[ResourceValue]) VALUES (2,'Admin.Settings.SystemSettings.SiteLanguageNote', 'Site language<br/>The setting allows you to change the language of the store (buttons, admin panel change, the products will remain in the language in which the store was filled).<br/><br/>  Instruction:<br/> <a href="https://www.advantshop.net/help/pages/change-buttons#4" target="_blank">https://www.advantshop.net/help/pages/change-buttons#4</a>')
INSERT INTO [Settings].[Localization] ([LanguageId],[ResourceKey],[ResourceValue]) VALUES (1,'Admin.SettingsCatalog.CatalogCommon.CountCategoriesInLineNote', 'Количество категорий в строке<br/>В данном поле укажите какое количество категорий необходимо выводить в каталоге в одной строке.<br/><br/> Инструкция:<br/><a href="https://www.advantshop.net/help/pages/template-settings#4" target="_blank">https://www.advantshop.net/help/pages/template-settings#4</a>')
