INSERT INTO [Settings].[Localization] ([LanguageId],[ResourceKey],[ResourceValue]) VALUES (1,'Admin.SettingsCatalog.CatalogCommon.ShowQuickViewNote', 'Быстрый просмотр<br/>Данная настройка позволяет просмотреть кратко карточку товара не переходя во внутрь')
