INSERT INTO [Settings].[Localization] ([LanguageId],[ResourceKey],[ResourceValue]) VALUES (1,'Admin.Settings.Catalog.SetAllProductsManualRatioNote', ' Рейтинг<br/>В данном поле пишите рейтинг, который можно установить массово для всех товаров. Значения указываются от 1 до 5. <br/><br/> Инструкция:<br/><a href="https://www.advantshop.net/help/pages/catalog-view#10" target="_blank">https://www.advantshop.net/help/pages/catalog-view#10</a>')
INSERT INTO [Settings].[Localization] ([LanguageId],[ResourceKey],[ResourceValue]) VALUES (2,'Admin.Settings.Catalog.SetAllProductsManualRatioNote', ' Rating<br/>In this field, write a rating that can be set in bulk for all products.Values ​​are from 1 to 5<br/><br/> Instruction:<br/><a href="https://www.advantshop.net/help/pages/catalog-view#10" target="_blank">https://www.advantshop.net/help/pages/catalog-view#10</a>')
INSERT INTO [Settings].[Localization] ([LanguageId],[ResourceKey],[ResourceValue]) VALUES (1,'Admin.SettingsCatalog.CatalogCommon.ShowFilterNote', 'Показывать фильтр в каталоге <br/>Настройка позволяет вывести фильтр со свойствами в каталог. Другими словами настройка включает работу фильтра в магазине.<br/><br/> Инструкция:<br/><a href="https://www.advantshop.net/help/pages/catalog-filtr#5" target="_blank">https://www.advantshop.net/help/pages/catalog-filtr#5 </a>')
INSERT INTO [Settings].[Localization] ([LanguageId],[ResourceKey],[ResourceValue]) VALUES (2,'Admin.SettingsCatalog.CatalogCommon.ShowFilterNote', 'Show filter in catalog <br/>Setting allows you to display a filter with properties in a directory. In other words, the setting includes the filter in the store. <br/><br/> Instruction:<br/><a href="https://www.advantshop.net/help/pages/catalog-filtr#5" target="_blank">https://www.advantshop.net/help/pages/catalog-filtr#5 </a>')
