INSERT INTO [Settings].[Localization] ([LanguageId],[ResourceKey],[ResourceValue]) VALUES (1,'Admin.Settings.Template.SocialWidget.ShowCommunicationWidget.HelpText', 'Показывать виджет коммуникаций<br/>Настройка включает отображение виджетов коммуникаций<br/><br/> Инструкция:<br/><a href="https://www.advantshop.net/help/pages/vidzhety-kommunikatsii" target="_blank">https://www.advantshop.net/help/pages/vidzhety-kommunikatsii</a>')
